    },
    {
      "name": "Nonlinear system",
      "entity_type": "task",
      "relation": "uses",
      "relevance": "The paper focuses on controlling networked systems with unknown nonlinear dynamics using advanced control techniques.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Nonlinear_system"
    },
    {
      "name": "Lyapunov function",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "Piecewise Lyapunov functionals are constructed to prove the stability and boundedness of system states and error signals.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Lyapunov_function"
    },
    {
      "name": "State observer",
      "entity_type": "method",
      "relation": "proposes",
      "relevance": "The paper proposes a nonlinear observer design to estimate unmeasured system states from available output measurements.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/State_observer"
    },
    {
      "name": "Feedback",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Output feedback control is employed to close the control loop using measured system outputs for controller design.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Feedback"
    },
    {
      "name": "Digital control",
      "entity_type": "method",
      "relation": "proposes",
      "relevance": "A digital controller is designed to reduce frequent control updates and handle intermittent packet transmission in networked systems.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Digital_control"
    },
    {
      "name": "Event-driven architecture",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Periodic event-triggered mechanisms are used to schedule intermittent packet transmission and improve communication efficiency.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Event-driven_architecture"
    }
  ]
}

