NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Microbiome",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper analyzes soil microbiomes under environmental stress to understand how communities respond to stress.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Microbiome"
    },
    {
      "name": "Network analysis",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "The paper uses network analysis to assess microbial community structure and identify network properties like modularity.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Network_analysis"
    },
    {
      "name": "Ecosystem services",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper focuses on how microbiome destabilization could undermine ecosystem services provided by microbial communities.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ecosystem_services"
    },
    {
      "name": "Biodiversity",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper measures changes in prokaryotic and fungal diversity as a key response to environmental stress.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Biodiversity"
    },
    {
      "name": "Modularity (networks)",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper identifies modularity as a key network property that declines with environmental stress.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Modularity_(networks)"
    },
    {
      "name": "Ecological stability",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper provides novel evidence that environmental stress destabilizes microbial networks, directly addressing ecosystem stability.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ecological_stability"
    },
    {
      "name": "Abiotic stress",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper examines how abiotic environmental stressors (elevation/water availability) affect microbial community properties.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Abiotic_stress"
    },
    {
      "name": "Soil microbiology",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper studies soil microbes and their network properties in response to environmental gradients.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Soil_microbiology"
    },
    {
      "name": "Stress gradient hypothesis",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper applies the stress gradient hypothesis to explain shifts in negative:positive biotic interactions under stress.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Stress_gradient_hypothesis"
    },
    {
      "name": "Community structure",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper proposes that environmental stress alters microbial community structure by changing compartmentalization and association patterns.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Community_structure"
    }
  ]
}

