NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Intention",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper builds on the logic of intentions framework, using intention-expressions as the foundation for analyzing moral ought.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Intention"
    },
    {
      "name": "Normative ethics",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The moral ought is a core normative ethical concept central to the paper's philosophical analysis.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Normative_ethics"
    },
    {
      "name": "Collective intentionality",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper proposes a novel account of we-intentions as a type of collective intentionality with special intersubjective properties.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Collective_intentionality"
    },
    {
      "name": "Intersubjectivity",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper argues that intersubjectivity is a critical missing element in analyzing the moral ought through intention-expressions.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Intersubjectivity"
    },
    {
      "name": "Egocentrism",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper contrasts ordinary egocentric intentions with we-intentions to explain why intentions alone cannot ground moral ought.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Egocentrism"
    },
    {
      "name": "Communitarianism",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper proposes a novel account of moral community based on shared we-intentions that define membership in the moral community.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Communitarianism"
    },
    {
      "name": "Rationality",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Rationality is foundational to the paper's account of moral community, which consists of rational beings sharing intrinsically reasonable intentions.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Rationality"
    },
    {
      "name": "Utilitarianism",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper references happiness maximization as a principle in the shared we-intention that constitutes membership in the moral community.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Utilitarianism"
    }
  ]
}

