NEW: {
  "paper_type": "survey",
  "triples": [
    {
      "name": "Extinction event",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Extinction_event",
      "relevance": "Paper analyzes the five historical mass extinction events and discusses evidence for a potential sixth extinction event caused by humans."
    },
    {
      "name": "Biodiversity",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Biodiversity",
      "relevance": "Paper discusses biodiversity crisis with increasing extinctions and plummeting abundances as evidence for the sixth mass extinction."
    },
    {
      "name": "IUCN Red List",
      "relation": "uses",
      "entity_type": "data",
      "wikipedia_url": "https://en.wikipedia.org/wiki/IUCN_Red_List",
      "relevance": "Paper critiques the IUCN Red List's heavy bias toward birds and mammals, noting its underestimation of invertebrate extinctions."
    },
    {
      "name": "Mollusca",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Mollusca",
      "relevance": "Paper uses molluscs as the primary example to extrapolate extinction rates, estimating 7.5-13% of known species have gone extinct since AD 1500."
    },
    {
      "name": "Natural history",
      "relation": "uses",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Natural_history",
      "relevance": "Paper advocates reviving natural history as essential for documenting and collecting species before they disappear."
    },
    {
      "name": "Taxonomy (biology)",
      "relation": "uses",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Taxonomy_(biology)",
      "relevance": "Paper emphasizes taxonomy as crucial for species classification and documentation in the face of the extinction crisis."
    },
    {
      "name": "Evolution",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Evolution",
      "relevance": "Paper addresses arguments that the extinction crisis represents a new trajectory of evolution and responds to those who accept it as natural change."
    },
    {
      "name": "Conservation biology",
      "relation": "uses",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Conservation_biology",
      "relevance": "Paper discusses conservation initiatives and their limitations, noting most are not species-oriented except for charismatic vertebrates."
    }
  ]
}

