  "paper_type": "contribution",
  "triples": [
    {
      "name": "Fitness landscape",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper quantifies how fitness effects of mutations change across genetic backgrounds and community composition, directly measuring the adaptive landscape.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Fitness_landscape"
    },
    {
      "name": "Mutation",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper analyzes fitness effects of all possible mutations through genome-wide transposon libraries.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Mutation"
    },
    {
      "name": "Fitness (biology)",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Fitness effects are the central measure used to quantify the adaptive landscape and evolutionary adaptation.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Fitness_(biology)"
    },
    {
      "name": "Ecosystem diversity",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper studies how ecological diversification affects the adaptive landscape in a nascent community.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ecosystem_diversity"
    },
    {
      "name": "Community (ecology)",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper analyzes how community composition affects fitness effects and the adaptive landscape.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Community_(ecology)"
    },
    {
      "name": "Escherichia coli",
      "entity_type": "artifact",
      "relation": "uses",
      "relevance": "E. coli is the model organism used in the Long-Term Evolution Experiment from which the strains for this study were derived.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Escherichia_coli"
    },
    {
      "name": "E. coli long-term evolution experiment",
      "entity_type": "data",
      "relation": "uses",
      "relevance": "The LTEE provides the source population and evolutionary context for the nascent L and S ecotypes studied in this work.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/E._coli_long-term_evolution_experiment"
    },
    {
      "name": "Gene knockout",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "The paper measures invasion fitness effects of all possible gene knockouts in the coexisting strains.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Gene_knockout"
    },
    {
      "name": "Transposon mutagenesis",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "The paper uses genome-wide barcoded transposon libraries to systematically create and measure knockouts.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Transposon_mutagenesis"
    },
    {
      "name": "Epistasis",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper reveals how epistatic effects (interactions between mutations) combine with ecological effects to shape the adaptive landscape.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Epistasis"
    },
    {
      "name": "DNA barcoding",
      "entity_type": "method",
      "relation": "proposes",
      "relevance": "The paper engineers novel genome-wide barcoded transposon libraries as a method to measure fitness effects across all possible knockouts.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/DNA_barcoding"
    }
  ]
}

