    {
      "name": "Collective motion",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "Paper proposes a new framework explaining how collective motion emerges in marching locusts through neural and consensus mechanisms.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Collective_motion"
    },
    {
      "name": "Self-propelled particles",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Paper contrasts their findings against classical self-propelled particle models from physics that cannot account for locust behavior.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Self-propelled_particles"
    },
    {
      "name": "Desert locust",
      "entity_type": "data",
      "relation": "uses",
      "relevance": "Desert locusts are the primary study organism used to investigate collective motion through field, lab, and virtual reality experiments.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Desert_locust"
    },
    {
      "name": "Swarm behaviour",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper studies swarming behavior in locusts and addresses how order emerges in locust swarms that affect millions of livelihoods.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Swarm_behaviour"
    },
    {
      "name": "Optomotor response",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Paper shows that while locusts respond to moving-dot stimuli through the optomotor response, this innate behavior does not mediate social response to neighbors.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Optomotor_response"
    },
    {
      "name": "Collective animal behavior",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Paper challenges long-held beliefs about how order emerges from disorder in animal collectives through novel understanding of locust behavior.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Collective_animal_behavior"
    }
  ]
}

