NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Data synchronization",
      "relation": "proposes",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Data_synchronization",
      "relevance": "LSL proposes a novel software-based approach to data synchronization across independent instruments using timestamps and network protocols."
    },
    {
      "name": "Clock synchronization",
      "relation": "proposes",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Clock_synchronization",
      "relevance": "LSL implements time synchronization across a local area network to ensure millisecond-precise synchronized recording across multiple devices."
    },
    {
      "name": "Multimodal learning",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Multimodal_learning",
      "relevance": "LSL enables multimodal recording by synchronizing data from multiple sensors and instruments capturing different data modalities simultaneously."
    },
    {
      "name": "Neurophysiology",
      "relation": "uses",
      "entity_type": "task",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Neurophysiology",
      "relevance": "LSL was built from the ground up for neurophysiological applications, enabling synchronized recording of neural and behavioral data."
    },
    {
      "name": "Brain–computer interface",
      "relation": "uses",
      "entity_type": "tool",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Brain%E2%80%93computer_interface",
      "relevance": "LSL supports brain-computer interface applications as part of its broader neurobehavioral recording ecosystem."
    },
    {
      "name": "Real-time computing",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Real-time_computing",
      "relevance": "LSL provides real-time analysis environments and ensures continuous, millisecond-precise data recording with real-time synchronization."
    },
    {
      "name": "Jitter",
      "relation": "proposes",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Jitter",
      "relevance": "LSL implements jitter compensation to account for network delays and variability, ensuring accurate synchronized recording."
    },
    {
      "name": "Data acquisition",
      "relation": "uses",
      "entity_type": "task",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Data_acquisition",
      "relevance": "LSL supports over 150 data acquisition device classes for synchronized collection of multimodal signals from various instruments."
    },
    {
      "name": "Timestamp",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Timestamp",
      "relevance": "LSL uses per-sample timestamps as the fundamental mechanism for synchronizing data streams across multiple independent instruments."
    },
    {
      "name": "Network Time Protocol",
      "relation": "uses",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Network_Time_Protocol",
      "relevance": "LSL leverages time synchronization concepts similar to NTP to coordinate timestamps across devices on a local area network."
    }
  ]
}

