    {
      "name": "Conservation law",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Conservation_law",
      "relevance": "Charge and dipole conservation laws are fundamental constraints that drive the Hilbert space fragmentation observed in the paper."
    },
    {
      "name": "Thermalization",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Thermalisation",
      "relevance": "The paper studies the breakdown of thermalization as a consequence of Hilbert space fragmentation in dipole-conserving systems."
    },
    {
      "name": "Hamiltonian (quantum mechanics)",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Hamiltonian_(quantum_mechanics)",
      "relevance": "Dipole-conserving Hamiltonians are the central objects of study for understanding fragmentation and non-thermal dynamics."
    },
    {
      "name": "Spin model",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Spin_model",
      "relevance": "The paper investigates one-dimensional spin-1 models to demonstrate Hilbert space fragmentation and ergodicity breaking."
    },
    {
      "name": "Quantum dynamics",
      "relation": "uses",
      "entity_type": "task",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_dynamics",
      "relevance": "The paper studies the out-of-equilibrium dynamics of quantum systems with constrained conserved quantities."
    },
    {
      "name": "Dipole moment",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Dipole_moment",
      "relevance": "Dipole moment conservation is a characteristic constraint of fracton systems that enables extensive Hilbert space fragmentation."
    },
    {
      "name": "Invariant subspace",
      "relation": "proposes",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Invariant_subspace",
      "relevance": "The paper identifies extensive fragmentation into exponentially many invariant subspaces as the mechanism for ergodicity breaking."
    },
    {
      "name": "Eigenstate thermalization hypothesis",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Eigenstate_thermalization_hypothesis",
      "relevance": "The paper shows that dipole conservation prevents eigenstate thermalization, leading to non-thermal eigenstates throughout the spectrum."
    },
    {
      "name": "Quantum random circuits",
      "relation": "uses",
      "entity_type": "method",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_random_circuits",
      "relevance": "Random unitary circuit models are used for comparison to validate the fragmentation and non-thermalization results."
    },
    {
      "name": "Many-body localization",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Many-body_localization",
      "relevance": "The paper's findings about fragmentation preventing thermalization relate to many-body localization phenomena."
    }
  ]
}

