NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Spontaneous symmetry breaking",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Fundamental concept underlying the phase transitions and ordered states demonstrated in the experiment.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Spontaneous_symmetry_breaking"
    },
    {
      "name": "Symmetry breaking",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Core theoretical framework for understanding the classification of phases of matter in the system.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Symmetry_breaking"
    },
    {
      "name": "Phase transition",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Describes the transitions between different ordered phases observed in the XY ferromagnet and antiferromagnet.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Phase_transition"
    },
    {
      "name": "Goldstone boson",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Gapless Goldstone modes emerge from continuous symmetry breaking and control thermodynamic stability of the ordered phase.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Goldstone_boson"
    },
    {
      "name": "Classical XY model",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "Paper realizes a two-dimensional dipolar XY model experimentally, advancing the understanding of XY interactions.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Classical_XY_model"
    },
    {
      "name": "Rydberg atom",
      "entity_type": "artifact",
      "relation": "uses",
      "relevance": "Rydberg atoms form the basis of the programmable quantum simulator used to realize the XY model.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Rydberg_atom"
    },
    {
      "name": "Quantum simulator",
      "entity_type": "tool",
      "relation": "proposes",
      "relevance": "Paper demonstrates a Rydberg quantum simulator for realizing the dipolar XY model and studying many-body physics.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_simulator"
    },
    {
      "name": "Ferromagnetism",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "XY ferromagnetism is one of the two phases studied, characterized by long-range order and dipolar interactions.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ferromagnetic_material"
    },
    {
      "name": "Antiferromagnetism",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "XY antiferromagnet is the complementary phase studied, showing alternating spin orientation patterns.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Antiferromagnetism"
    },
    {
      "name": "Ising model",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Contrasts with the XY model; paper notes previous Rydberg work realized Ising-type interactions showing discrete spin symmetry.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ising_model"
    },
    {
      "name": "Magnetic dipole–dipole interaction",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Long-range dipolar interactions are essential for the XY model realization and enable features prohibited without them.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Magnetic_dipole–dipole_interaction"
    },
    {
      "name": "Quantum phase transition",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Adiabatic preparation explores transitions between different quantum phases of the XY model.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_phase_transition"
    }
  ]
}

