    },
    {
      "name": "IUCN Red List",
      "relation": "uses",
      "entity_type": "data",
      "wikipedia_url": "https://en.wikipedia.org/wiki/IUCN_Red_List",
      "relevance": "The paper critically examines the IUCN Red List's limitations in assessing extinction rates, noting its bias toward vertebrates."
    },
    {
      "name": "Background extinction rate",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Background_extinction_rate",
      "relevance": "The paper compares current extinction rates to background rates to argue that a sixth mass extinction is underway."
    },
    {
      "name": "Mollusca",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Mollusca",
      "relevance": "Molluscs are used as the primary example to extrapolate global extinction rates, with estimates of 7.5-13% extinction since 1500."
    },
    {
      "name": "Conservation biology",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Conservation_biology",
      "relevance": "Conservation initiatives and criteria are discussed as inadequate responses to the extinction crisis, particularly for invertebrates."
    },
    {
      "name": "Natural history",
      "relation": "proposes",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Natural_history",
      "relevance": "The paper advocates for reviving natural history and specimen collection as essential practices to document species before extinction."
    },
    {
      "name": "Taxonomy",
      "relation": "proposes",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Taxonomy",
      "relevance": "The paper calls for renewed emphasis on taxonomic study as essential for identifying and documenting Earth's biodiversity."
    },
    {
      "name": "Endemism",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Endemism",
      "relevance": "Island endemic species are discussed as facing disproportionately higher extinction rates compared to continental species."
    },
    {
      "name": "Marine invertebrates",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Marine_invertebrates",
      "relevance": "The paper examines whether marine invertebrates face the same crisis level as non-marine biota in the current extinction event."
    }
  ]
}

