    },
    {
      "name": "Network complexity",
      "relation": "proposes",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Network_complexity",
      "relevance": "Paper identifies that land use conversion increases network complexity metrics in microbial communities."
    },
    {
      "name": "Robustness of complex networks",
      "relation": "proposes",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Robustness_of_complex_networks",
      "relevance": "Paper demonstrates increased network robustness and decreased vulnerability in agricultural systems."
    },
    {
      "name": "Biodiversity",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Biodiversity",
      "relevance": "Paper contrasts reduced microbial biodiversity in cropland with increased network complexity."
