    {
      "name": "Grassland",
      "relation": "uses",
      "entity_type": "data",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Grassland",
      "relevance": "Native tallgrass prairie serves as baseline ecosystem for comparison with converted agricultural land."
    },
    {
      "name": "Ecological network",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ecological_network",
      "relevance": "Paper applies molecular ecological network analysis to study species interactions in soil communities."
    },
    {
      "name": "Biological interaction",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Biological_interaction",
      "relevance": "Paper studies how agricultural disturbance alters species interactions and network relationships."
    },
    {
      "name": "Ecosystem",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ecosystem",
      "relevance": "Paper analyzes ecosystem-level processes including network stability and function maintenance under disturbance."
    }
  ]
}

