NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Species diversity",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper addresses how diverse ecosystems maintain stability, building on foundational concepts of species diversity.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Species_diversity"
    },
    {
      "name": "Ecological stability",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper explains mechanisms underlying ecosystem stability across different diversity levels.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ecological_stability"
    },
    {
      "name": "Power law",
      "entity_type": "method",
      "relation": "proposes",
      "relevance": "The paper proposes that growth scales as a sublinear power law, a novel contribution to understanding ecosystem dynamics.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Power_law"
    },
    {
      "name": "Density dependence",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The sublinear growth model exhibits density-dependent self-regulation, a novel mechanism proposed in this paper.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Density_dependence"
    },
    {
      "name": "Competition (biology)",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper analyzes how competitive interactions among populations change with different growth models.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Competition_(biology)"
    },
    {
      "name": "Logistic function",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "The paper contrasts its sublinear growth model with logistic growth, the standard population ecology model.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Logistic_function"
    },
    {
      "name": "Competitive exclusion principle",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper addresses how sublinear growth prevents competitive exclusion, contrasting with this classical ecological principle.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Competitive_exclusion_principle"
    },
    {
      "name": "Biodiversity loss",
      "entity_type": "task",
      "relation": "uses",
      "relevance": "The paper addresses the worldwide loss of species diversity as the motivation for understanding ecosystem stability.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Biodiversity_loss"
    },
    {
      "name": "Macroecology",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper predicts large-scale macroecological patterns emerging from its theoretical model.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Macroecology"
    }
  ]
}

