NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Hilbert space",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The fundamental mathematical framework for quantum systems in which Hilbert space fragmentation occurs.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Hilbert_space"
    },
    {
      "name": "Ergodicity",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper shows ergodicity breaking as a consequence of Hilbert space fragmentation in dipole-conserving systems.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ergodicity"
    },
    {
      "name": "Charge conservation",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "One of the key conservation laws (charge/total Sz) that constrains the system dynamics in the studied models.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Charge_conservation"
    },
    {
      "name": "Fracton",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Fracton systems exhibit the characteristic charge and dipole conservation properties that the paper analyzes.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Fracton_(subdimensional_particle)"
    },
    {
      "name": "Spin model",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper investigates one-dimensional spin-1 models as concrete examples of dipole-conserving systems.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Spin_model"
    },
    {
      "name": "Thermalisation",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper studies the breakdown of thermalization (non-thermal behavior) arising from Hilbert space fragmentation.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Thermalisation"
    },
    {
      "name": "Autocorrelation",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "Infinite temperature auto-correlation measurements are used to demonstrate non-thermal behavior in the minimal model.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Autocorrelation"
    },
    {
      "name": "Quantum random circuits",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Random unitary circuit models with dipole conservation are compared to the spin-1 models for validation.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_random_circuits"
    },
    {
      "name": "Eigenstate",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper examines non-thermal eigenstates appearing throughout the energy spectrum due to Hilbert space fragmentation.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Eigenstate_thermalization_hypothesis"
    }
  ]
}

