NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Spontaneous symmetry breaking",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Spontaneous_symmetry_breaking",
      "relevance": "The paper studies continuous spontaneous symmetry breaking in the XY model, a fundamental concept in classifying phases of matter."
    },
    {
      "name": "Phase transition",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Phase_transition",
      "relevance": "Phase transitions are central to understanding the behavior of matter and the paper demonstrates transitions between ferromagnetic and antiferromagnetic phases."
    },
    {
      "name": "Goldstone boson",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Goldstone_boson",
      "relevance": "Gapless Goldstone modes emerge from continuous symmetry breaking and control thermodynamic stability, which the paper explores experimentally."
    },
    {
      "name": "Classical XY model",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Classical_XY_model",
      "relevance": "The paper realizes a two-dimensional dipolar XY model with continuous spin-rotational symmetry as its main physical system."
    },
    {
      "name": "Ferromagnetism",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ferromagnetism",
      "relevance": "The XY ferromagnet is one of the two phases demonstrated and characterized in the paper for its long-range order."
    },
    {
      "name": "Antiferromagnetism",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Antiferromagnetism",
      "relevance": "The XY antiferromagnet is the complementary phase to the ferromagnet, both prepared adiabatically in the Rydberg array."
    },
    {
      "name": "Quantum simulator",
      "relation": "proposes",
      "entity_type": "artifact",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_simulator",
      "relevance": "The paper proposes and uses a programmable Rydberg quantum simulator to experimentally realize the two-dimensional XY model."
    },
    {
      "name": "Rydberg atom",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Rydberg_atom",
      "relevance": "Rydberg atoms form the basis of the programmable quantum simulator used to create the dipolar XY model with long-range interactions."
    },
    {
      "name": "Magnetic dipole–dipole interaction",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Magnetic_dipole–dipole_interaction",
      "relevance": "Long-range dipolar interactions are essential to the paper's realization of the XY model and enable long-range order without disorder."
    },
    {
      "name": "Long-range order",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Long-range_order",
      "relevance": "The paper characterizes the presence of long-range XY order in the ferromagnetic phase, a feature prohibited without long-range dipolar interaction."
    },
    {
      "name": "Ising model",
      "relation": "uses",
      "entity_type": "concept",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Ising_model",
      "relevance": "The paper references the Ising model's discrete symmetry breaking as a contrast to the continuous symmetry breaking studied in the XY model."
    }
  ]
}

