NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Mpemba effect",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper studies a quantum version of the classical Mpemba effect, where hot water can freeze faster than warm water.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Mpemba_effect"
    },
    {
      "name": "Integrable system",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper analyzes the quantum Mpemba effect specifically in closed many-body integrable systems with U(1) conserved charges.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Integrable_system"
    },
    {
      "name": "Entropy of entanglement",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper uses entanglement asymmetry, a recently introduced measure related to entanglement entropy, to establish criteria for the quantum Mpemba effect.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Entropy_of_entanglement"
    },
    {
      "name": "Cellular automaton",
      "entity_type": "artifact",
      "relation": "uses",
      "relevance": "The Rule 54 cellular automaton is used as a specific example system to demonstrate and test the quantum Mpemba effect criteria.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Cellular_automaton"
    },
    {
      "name": "Fermion",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Free fermion models are explicitly used as an example system to analyze the quantum Mpemba effect.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Fermion"
    },
    {
      "name": "Lieb–Liniger model",
      "entity_type": "artifact",
      "relation": "uses",
      "relevance": "The Lieb-Liniger model (a 1D Bose gas with delta interactions) serves as another key example system for demonstrating the quantum Mpemba effect.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Lieb–Liniger_model"
    }
  ]
}

