    },
    {
      "name": "Multi-agent system",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper examines multi-agent systems composed of LLMs to understand how populations of interacting agents develop social conventions.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Multi-agent_system"
    },
    {
      "name": "AI alignment",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper addresses implications for designing AI systems that align with human values and remain aligned through decentralized social coordination mechanisms.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/AI_alignment"
    }
  ]
}

