NEW: {
  "paper_type": "contribution",
  "triples": [
    {
      "name": "Quantum chaos",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper characterizes quantum chaos in many-body systems and proposes KCP as an order parameter to distinguish chaotic from integrable behavior.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Quantum_chaos"
    },
    {
      "name": "Random matrix",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Spectral statistics, a key probe used in the paper to validate KCP, is fundamentally based on random matrix theory.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Random_matrix"
    },
    {
      "name": "Correlation function (quantum field theory)",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "Out-of-time-order correlators (OTOCs), another diagnostic used to validate KCP, are a type of correlation function in quantum systems.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Correlation_function_(quantum_field_theory)"
    },
    {
      "name": "Sachdev–Ye–Kitaev model",
      "entity_type": "artifact",
      "relation": "uses",
      "relevance": "The paper tests the KCP diagnostic on the mass-deformed and sparse variants of the SYK model, making it a central experimental system.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Sachdev–Ye–Kitaev_model"
    },
    {
      "name": "Phase transition",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper proposes KCP as an order parameter, a concept from phase transition theory, to characterize quantum chaos.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Phase_transition"
    },
    {
      "name": "Many-body problem",
      "entity_type": "task",
      "relation": "uses",
      "relevance": "The paper studies Krylov complexity in many-body quantum systems to characterize chaotic and integrable transitions.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Many-body_problem"
    },
    {
      "name": "Integrable system",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper investigates chaotic-integrable transitions by comparing KCP behavior across integrable and chaotic quantum systems.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Integrable_system"
    },
    {
      "name": "Krylov subspace",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper introduces Krylov complexity as a new diagnostic for quantum chaos, drawing on the mathematical concept of Krylov subspaces.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Krylov_subspace"
    }
  ]
}

