      "wikipedia_url": "https://en.wikipedia.org/wiki/Citizens'_assembly"
    },
    {
      "name": "Deliberative opinion poll",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "Deliberative opinion polls are a practical implementation of deliberative democracy that the paper builds upon.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Deliberative_opinion_poll"
    },
    {
      "name": "Civic engagement",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper examines civic engagement through deliberation and its impact on voter behavior and participation.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Civic_engagement"
    },
    {
      "name": "Voting behavior",
      "entity_type": "concept",
      "relation": "uses",
      "relevance": "The paper investigates how deliberative processes influence voting behavior and voter decision-making.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Voting_behavior"
    },
    {
      "name": "Field experiment",
      "entity_type": "method",
      "relation": "uses",
      "relevance": "The paper employs field experiments with public deliberation and one-year follow-up to test effects on voter behavior.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Field_experiment"
    },
    {
      "name": "Elections in the United States",
      "entity_type": "task",
      "relation": "uses",
      "relevance": "The paper tests its deliberation intervention in the context of mid-term U.S. elections.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Elections_in_the_United_States"
    },
    {
      "name": "Scaling deliberation for broader society",
      "entity_type": "concept",
      "relation": "proposes",
      "relevance": "The paper proposes using organized deliberative processes scaled beyond mini-publics to create more deliberative voters.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Deliberative_democracy"
    },
    {
      "name": "Automated deliberation using technology",
      "entity_type": "method",
      "relation": "proposes",
      "relevance": "The paper investigates whether technology can assist with scaling organized deliberative processes.",
      "wikipedia_url": "https://en.wikipedia.org/wiki/Online_deliberation"
    }
  ]
}

